'''
Advent of Code Day 1 solution
'''

from random import randint

def generate_directions(max_length:int):
    directions = ""
    for i in range(max_length):
        if randint(1, 10) <= 5:
            directions += "+"
        else:
            directions += "-"
    return directions

def find_floor(directions):
    floor = 0
    for ch in directions:
        if ch == "+" or ch == "(":
            floor += 1
        else:
            floor -= 1
    return floor

def find_basement(directions: str):
    floor = 0
    char_pos = 0
    while floor != -1:
        ch = directions[char_pos]
        if ch == "(":
            floor += 1
        else:
            floor -= 1
        char_pos += 1
    return char_pos

if __name__ == "__main__":
    directions_list = [
        "++--+-",
        "+-+-",
        "++-++--",
        "--+++-+",
        "---",
        "-+--+--"
    ]

    for directions in directions_list:
        print(f"`{directions}` brings you to floor {find_floor(directions)}")

    print()

    for i in range(10):
        directions = generate_directions(randint(100, 120))
        print(f"`{directions}` brings you to floor {find_floor(directions)}")


